      out.write("//페이징\n");
      out.write("function paging() {\n");
      out.write("  var rowPerPage = 5;\n");
      out.write("  $('#nav').remove();\n");
      out.write("  var $members = $('#members');\n");
      out.write("  $members.after('<div id=\"nav\" style=\"margin: auto;\">');\n");
      out.write("\n");
      out.write("  var $tr = $($members).find('tbody tr');\n");
      out.write("  var rowTotals = $tr.length;\n");
      out.write("\n");
      out.write("  var pageTotal = Math.ceil(rowTotals / rowPerPage);\n");
      out.write("  var i = 0;\n");
      out.write("\n");
      out.write("  for (; i < pageTotal; i++) {\n");
      out.write("    $('<a href=\"#\"></a>').attr('rel', i).html(i + 1).appendTo('#nav');\n");
      out.write("  }\n");
      out.write("\n");
      out.write("  $tr.addClass('off-screen').slice(0, rowPerPage).removeClass('off-screen');\n");
      out.write("\n");
      out.write("  var $pagingLink = $('#nav a');\n");
      out.write("\n");
      out.write("  $pagingLink.on('click', function(evt) {\n");
      out.write("    evt.preventDefault();\n");
      out.write("    var $this = $(this);\n");
      out.write("\n");
      out.write("    if ($this.hasClass('active')) {\n");
      out.write("      return;\n");
      out.write("    }\n");
      out.write("\n");
      out.write("    $pagingLink.removeClass('active');\n");
      out.write("    $this.addClass('active');\n");
      out.write("\n");
      out.write("    var currPage = $this.attr('rel');\n");
      out.write("    var startItem = currPage * rowPerPage;\n");
      out.write("    var endItem = startItem + rowPerPage;\n");
      out.write("\n");
      out.write("    $tr.css('opacity', '0.0').addClass('off-screen').slice(startItem, endItem).removeClass('off-screen').animate({\n");
      out.write("      opacity: 1\n");
      out.write("    }, 300);\n");
      out.write("\n");
      out.write("  });\n");
      out.write("\n");
      out.write("  $pagingLink.filter(':first').addClass('active');\n");
      out.write("\n");
      out.write("};\n");
      out.write("</script>\n");
      out.write("</html>\n");
