mapper.select:
SELECT *
    FROM MEMBER
WHERE 1=1

    AND   MEMBER_ID 	= #{memberId}


    AND   MEMBER_NM 	= #{memberNm}


    AND   START_DATE 	= #{startDate}


    AND   END_DATE 	= #{endDate}


    AND   SEX 			= #{sex}


    AND   COUNTRY 		= #{country}


    AND   CITY 		= #{city}

ORDER BY START_DATE DESC

mapper.insert:
INSERT INTO MEMBER
    (
        MEMBER_ID, MEMBER_NM, START_DATE, SEX ,
        COUNTRY, CITY
    )
VALUES
    (
        #{memberId}, #{memberNm}, TO_CHAR(SYSDATE,'YYYY-MM-DD'), #{sex} ,
        #{country}, #{city}
    )

mapper.delete:
DELETE FROM MEMBER
WHERE MEMBER_ID IN

                #{memberId}
